INSERT INTO jobs (id, job_id, title, company_name, business_name, company_email, bid, location, city, state, country, remote_work, job_type, department, description, requirements, benefits, qualification, skills_required, salary_min, salary_max, salary_currency, hide_salary, screening_questions, hiring_process, application_instructions, status, applicants, posted_date, updated_date, closing_date) VALUES
(1, 'J000001', 'Senior Java Developer', 'TechCorp Solutions', 'TechCorp Solutions', 'hr@techcorp.com', 'B001', 'San Francisco, CA', 'San Francisco', 'CA', 'USA', true, 'Full-time', 'Engineering', 'We are looking for an experienced Java developer to join our team and work on cutting-edge enterprise applications.', '5+ years of Java development experience, Spring Boot proficiency, Microservices experience', 'Health insurance, 401k matching, Flexible work hours, Remote work options', 'Bachelor degree in Computer Science or related field', 'Java,Spring Boot,Microservices,Docker,AWS', 120000.00, 160000.00, 'USD', false, '["What is your experience with Spring Boot?","Describe a challenging project you worked on"]', 'Application Review -> Technical Interview -> Final Interview -> Offer', 'Please include your GitHub profile and a code sample', 'Active', 2, DATE_SUB(NOW(), INTERVAL 7 DAY), NOW(), DATE_ADD(NOW(), INTERVAL 30 DAY)),
(2, 'J000002', 'Product Manager', 'InnovateTech', 'InnovateTech', 'careers@innovatetech.com', 'B002', 'Austin, TX', 'Austin', 'TX', 'USA', false, 'Full-time', 'Product', 'Join our product team to drive product strategy and development for our innovative software solutions.', '3+ years product management experience, Experience with Agile methodologies, Technical background preferred', 'Competitive salary, Stock options, Health benefits, Learning budget', 'Bachelor degree required, MBA preferred', 'Product Management,Agile,SQL,Analytics', 90000.00, 130000.00, 'USD', false, '["What product have you launched?","How do you prioritize features?"]', 'Resume Review -> Product Case Study -> Leadership Interview -> Offer', 'Include links to products you have launched', 'Active', 1, DATE_SUB(NOW(), INTERVAL 5 DAY), NOW(), DATE_ADD(NOW(), INTERVAL 25 DAY)),
(3, 'J000003', 'UX Designer', 'StartupCo', 'StartupCo', 'jobs@startupco.com', 'B003', 'New York, NY', 'New York', 'NY', 'USA', true, 'Full-time', 'Design', 'We are seeking a talented UX Designer to create intuitive and beautiful user experiences for our FinTech platform.', '3+ years UX design experience, Proficiency in Figma and design systems, Portfolio required', 'Equity package, Health insurance, Flexible hours, Co-working space', 'Bachelor in Design or related field', 'UI/UX Design,Figma,User Research,Design Systems', 70000.00, 100000.00, 'USD', false, '["Show us your design process","What is your favorite design tool?"]', 'Portfolio Review -> Design Challenge -> Team Interview -> Offer', 'Please include your portfolio link and design case studies', 'Active', 0, DATE_SUB(NOW(), INTERVAL 3 DAY), NOW(), DATE_ADD(NOW(), INTERVAL 20 DAY)),
(4, 'J000004', 'Data Analyst', 'TechCorp Solutions', 'TechCorp Solutions', 'hr@techcorp.com', 'B001', 'Remote', 'San Francisco', 'CA', 'USA', true, 'Full-time', 'Analytics', 'Looking for a data analyst to help us make data-driven decisions and create insightful dashboards.', '2+ years data analysis experience, SQL proficiency, Experience with BI tools like Tableau or Power BI', 'Remote work, Learning opportunities, Health benefits, Flexible schedule', 'Bachelor in Mathematics, Statistics, or related field', 'SQL,Python,Tableau,Excel,Statistics', 65000.00, 85000.00, 'USD', false, '["What SQL queries have you written?","Experience with data visualization?"]', 'Technical Assessment -> Data Analysis Interview -> Final Interview', 'Include SQL queries you have written', 'Active', 3, DATE_SUB(NOW(), INTERVAL 10 DAY), NOW(), DATE_ADD(NOW(), INTERVAL 35 DAY)),
(5, 'J000005', 'DevOps Engineer', 'InnovateTech', 'InnovateTech', 'careers@innovatetech.com', 'B002', 'Austin, TX', 'Austin', 'TX', 'USA', false, 'Full-time', 'Engineering', 'We need a DevOps engineer to help us build and maintain our cloud infrastructure and CI/CD pipelines.', '4+ years DevOps/Infrastructure experience, AWS/Azure experience, Container orchestration knowledge', 'Competitive salary, Cloud certifications paid, Flexible work, Learning budget', 'Bachelor in Computer Science or related field', 'AWS,Docker,Kubernetes,Jenkins,Python', 95000.00, 125000.00, 'USD', false, '["What cloud platforms have you worked with?","Experience with Kubernetes?"]', 'Technical Interview -> Infrastructure Design -> Team Fit Interview', 'Include links to infrastructure projects', 'Active', 1, DATE_SUB(NOW(), INTERVAL 8 DAY), NOW(), DATE_ADD(NOW(), INTERVAL 28 DAY));
